const { gql } = require('apollo-server-express');

module.exports  = gql`
type File {
  id: String!
  filename: String!
  mimetype: String!
  encoding: String!
  imageSize: ImageSize!
}

type ImageSize {
  width: Int!
  height: Int!
}

type Picture {
  id: String!
  imageSize: ImageSize!
}

type Query {
  pictures: [Picture]
}

type Mutation {
  singleUpload(file: Upload!): File!
}
`;